import React, { Component } from 'react';
import { bindActionCreators } from 'redux';
import { Alert } from 'react-bootstrap';
import { connect } from 'react-redux';
import { actionCreators } from '../store/Home';


class Home extends Component {

    handleChange(e) {
        const { name, value } = e.target;
        this.props.changeField(name, value);
    }

    handleSubmit(e) {
        e.preventDefault();
        const { username, password } = this.props;
        this.props.login(username, password);
    }

    render() {
        const { username, password, submitted, loading, error } = this.props;
        return (
            <div className="col-md-6 col-md-offset-3">
                <h2>Login</h2>
                {error &&
                    <Alert bsStyle="danger">{error}</Alert>
                }
                <form name="form" onSubmit={(e) => this.handleSubmit(e)}>
                    <div className={'form-group' + (submitted && !username ? ' has-error' : '')}>
                        <label htmlFor="username">Username</label>
                        <input type="text" className="form-control" name="username" value={username} onChange={(e) => this.handleChange(e)} />
                        {submitted && !username &&
                            <div className="help-block">Username is required</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label htmlFor="password">Password</label>
                        <input type="password" className="form-control" name="password" value={password} onChange={(e) => this.handleChange(e)} />
                        {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary">Login</button>
                        {loading &&
                            <img alt="Loading" src="data:image/gif;base64," />
                        }
                    </div>
                </form>
            </div>
        );
    }
}

export default connect(
    state => state.home,
    dispatch => bindActionCreators(actionCreators, dispatch)
)(Home);